$(document).ready(function(){$(".accordion-tabs").each(function(){$(this).children("li").first().children("a").addClass("is-active").next().addClass("is-open").show()}),$(".accordion-tabs").on("click","li > a.tab-link",function(s){if($(this).hasClass("is-active"))s.preventDefault();else{s.preventDefault();var i=$(this).closest(".accordion-tabs");i.find(".is-open").removeClass("is-open").hide(),$(this).next().toggleClass("is-open").toggle(),i.find(".is-active").removeClass("is-active"),$(this).addClass("is-active")}})});